<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css">
<style>
  /* Base Styles */
  body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    line-height: 1.6;
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
    color: #333;
  }

  /* Header */
  .header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 30px;
    padding-bottom: 15px;
    border-bottom: 2px solid #eaeaea;
  }

  .logo {
    font-size: 24px;
    font-weight: bold;
    color: #2c3e50;
  }

  /* Search Box */
  .search-container {
    margin-bottom: 30px;
  }

  #searchInput {
    width: 100%;
    padding: 12px 20px;
    border: 1px solid #ddd;
    border-radius: 4px;
    font-size: 16px;
    box-sizing: border-box;
  }

  /* Year Group */
  .year-group {
    margin-bottom: 40px;
  }

  .year-header {
    font-size: 28px;
    margin-bottom: 20px;
    color: #2c3e50;
    padding-bottom: 8px;
    border-bottom: 1px solid #eaeaea;
  }

  /* Publication Item */
  .publication {
    background-color: #f9f9f9;
    border-radius: 8px;
    padding: 20px;
    margin-bottom: 20px;
    box-shadow: 0 2px 5px rgba(0,0,0,0.05);
    transition: transform 0.2s, box-shadow 0.2s;
  }

  .publication:hover {
    transform: translateY(-3px);
    box-shadow: 0 5px 15px rgba(0,0,0,0.1);
  }

  .pub-title {
    font-size: 20px;
    font-weight: 600;
    margin-bottom: 8px;
    color: #2980b9;
  }

  .pub-title a {
    color: inherit;
    text-decoration: none;
  }

  .pub-title a:hover {
    text-decoration: underline;
  }

  .pub-authors {
    color: #555;
    margin-bottom: 8px;
    font-style: italic;
  }

  .pub-venue {
    background-color: #e3f2fd;
    color: #1976d2;
    padding: 4px 8px;
    border-radius: 4px;
    display: inline-block;
    margin-bottom: 12px;
    font-weight: 500;
  }

  .pub-links {
    display: flex;
    gap: 15px;
  }

  .pub-links a {
    color: #7f8c8d;
    text-decoration: none;
    transition: color 0.2s;
  }

  .pub-links a:hover {
    color: #2980b9;
  }

  .pub-links i {
    margin-right: 5px;
  }

  /* Responsive */
  @media (max-width: 768px) {
    .header {
      flex-direction: column;
      text-align: center;
    }

    .logo {
      margin-bottom: 15px;
    }

    .pub-title {
      font-size: 18px;
    }
  }
</style>

